// IN DAE > Set the stackable to "Stacking effects apply the effect multiple times"
async function wait(ms) { return new Promise(resolve => { setTimeout(resolve, ms); }); }
const lastArg = args[args.length - 1];
let tactor;
if (lastArg.tokenId) tactor = canvas.tokens.get(lastArg.tokenId).actor;
else tactor = game.actors.get(lastArg.actorId);
const itemD = lastArg.efData.flags.dae.itemData;

if(args[0] === "on"){
    let itemData = [{
  "name": "Starry Archer Shot",
  "type": "feat",
  "img": "icons/magic/light/beam-layered-teal.webp",
  "data": {
    "description": {
      "value": "<p>A constellation of an archer appears on you. When you activate this form, and as a bonus action on your subsequent turns while it lasts, you can make a ranged spell attack, hurling a luminous arrow that targets one creature within 60 feet of you. On a hit, the attack deals radiant damage equal to [[/r 1d8]] + your Wisdom modifier.</p>"
    },    
    "identified": true,
    "activation": {
      "type": "bonus",
      "cost": 1,
      "condition": ""
    },
    "target": {
      "value": 1,
      "width": null,
      "units": "",
      "type": "creature"
    },    
    "range": {
      "value": 60,
      "long": null,
      "units": "ft"
    },
    "uses": {
      "value": null,
      "max": "",
      "per": ""
    },
    "ability": "wis",
    "actionType": "rsak",
    "attackBonus": `0`,
    "damage": {
      "parts": [
        [
          "1d8 + @abilities.wis.mod",
          "radiant"
        ]
      ],
      "versatile": ""
    }
  },  
  "flags": {    
    "midi-qol": {
      "onUseMacroName": "",
      "effectActivation": false
    }
  }
}];
    await tactor.createEmbeddedDocuments("Item", itemData);
}
if (args[0] === "off") {
    let itemz = tactor.data.items.find(i => i.name === "Starry Archer Shot");
console.log(itemD);
console.log(itemz);
    if (itemz) await tactor.deleteEmbeddedDocuments('Item', [itemz.id]);
}
