data.command:
const workflow = MidiQOL.Workflow.getWorkflow(args[0].uuid);
const ac = args[0].targets[0].actor.data.data.attributes.ac.value;
const roll = workflow.attackTotal;
if (workflow.isFumble || !args[0].targets.length || roll >= ac) return {};
const tokD = canvas.tokens.get(args[0].tokenId);
const actD = tokD.actor;
const ki_item = actD.items.getName("Ki");
const ki_cnt = ki_item.data.data.uses.value;

if (ki_cnt === 0) return {};

if (roll < ac) { //&& (roll + 3 >= ac)) {

    let option = "";
    const z = {};
    if (ki_cnt >= 1)
        z.one = {
            label: "1 Ki: +2",
            callback: () => {
                option = 1;
                bonus = 2;
            }
        };
    if (ki_cnt >= 2)
        z.two = {
            label: "2 Ki: +4",
            callback: () => {
                option = 2;
                bonus = 4;
            }
        };
    if (ki_cnt >= 3)
        z.three = {
            label: "3 Ki: +6",
            callback: () => {
                option = 3;
                bonus = 6;
            }
        };
    z.cancel = {
        label: "Cancel",
        callback: () => {
            option = 0;
        }
    };


    let bonus = 0;
    ChatMessage.create({
        whisper: ChatMessage.getWhisperRecipients("GM"),
        user: game.user.id,
        speaker: ChatMessage.getSpeaker(),
        content: `${actD.data.name} considers using ki to focusing their aim`
    })
    let dialog = new Promise((resolve, reject) => {
      new Dialog({
        title: `Focused Aim`,
        content: `<p><i>When you miss with an attack roll, you can spend 1 to 3 ki points to increase your attack roll by 2 for each of these ki points you spend, potentially turning the miss into a hit.</i></p>

        <p>Your attack roll is currently <b>${roll}</b> and you have <b>${ki_cnt}/${ki_item.data.data.uses.max}</b> ki remaining.</p>`,
        buttons: z,
        default: "cancel",
        close: (html) => {
            if (!option) {resolve(false)
                return {}
            }resolve(true);
            // other things happen
        }
    }).render(true);
    })

    useFA = await dialog;
    if (!useFA) return {};
    ChatMessage.create({
        whisper: ChatMessage.getWhisperRecipients("GM"),
        user: game.user.id,
        speaker: ChatMessage.getSpeaker(),
        content: `${actD.data.name} uses ${(bonus / 2)} ki to give +${bonus} to their attack`
    })
    workflow.attackRoll = await new Roll(workflow.attackRoll.result + ` + ${bonus}[Focused Aim]`).roll();
    workflow.attackTotal = workflow.attackRoll.total;
    workflow.attackRollHTML = await workflow.attackRoll.render();
    await actD.items.getName("Ki").update({
        'data.uses.value': ki_cnt - (bonus / 2)
    });

}
