data.command:
// You can do a short cut version
const a = canvas.tokens.controlled[0]?.actor || game.user.character;
let the_message = `<em>${actor.name} acts cunningly.</em>`;



new Dialog({
	title: `Cunning Action`,
	content: `<p>Select an action type Dash, Disengage, or Hide action.</p>`,
	buttons: {
		one: {label: "Dash", callback: () => {
            // do stuff here
const actorD = game.actors.get(args[0].actor._id);
const effectData = [{
        changes: [
            { key: "data.attributes.movement.walk", mode: 1, value: 2, priority: 20 },
        ],
        origin: args[0].uuid,
        disabled: false,
        duration: { turns: 1, startTime: game.time.worldTime },
        icon: "data.attributes.movement.walk",
        label: `Dashing`
    }]
    actorD.createEmbeddedDocuments("ActiveEffect", effectData);
                the_message = `<em>${actor.name} decides to @action[Dash].</em>`;
ChatMessage.create({
            user: game.user._id,
            speaker: ChatMessage.getSpeaker({token: a}),
            content: the_message,
            type: CONST.CHAT_MESSAGE_TYPES.EMOTE
         });
        }},
		two: {label: "Disengage", callback: () => {
            the_message = `<em>${actor.name} decides to @action[Disengage].</em>`;
ChatMessage.create({
            user: game.user._id,
            speaker: ChatMessage.getSpeaker({token: a}),
            content: the_message,
            type: CONST.CHAT_MESSAGE_TYPES.EMOTE
         });
        }},
		three: {label: "Hide", callback: () => {
            actor.rollSkill('ste');
            the_message = `<em>${actor.name} decides to @action[Hide].</em>`;
ChatMessage.create({
            user: game.user._id,
            speaker: ChatMessage.getSpeaker({token: a}),
            content: the_message,
            type: CONST.CHAT_MESSAGE_TYPES.EMOTE
         });
        }}
	},
    default: "one"
}).render(true);
