data.command:
// IN DAE > Set the stackable to "Stacking effects apply the effect multiple times"
async function wait(ms) { return new Promise(resolve => { setTimeout(resolve, ms); }); }
const lastArg = args[args.length - 1];
let tactor;
if (lastArg.tokenId) tactor = canvas.tokens.get(lastArg.tokenId).actor;
else tactor = game.actors.get(lastArg.actorId);
const itemD = lastArg.efData.flags.dae.itemData;
let modtype = tactor.data.data.attributes.spellcasting;
let cast_mod = tactor.data.data.abilities[modtype].mod;

if(args[0] === "on"){
    let itemData = [{
  "name": itemD.name,
  "type": "consumable",
  "img": "modules/plutonium/media/icon/spell/xge-magic-stone.webp",
  "data": {
    "description": {
      "value": "<p>You touch one to three pebbles and imbue them with magic. You or someone else can make a ranged spell attack with one of the pebbles by throwing it or hurling it with a @item[sling|phb]. If thrown, a pebble has a range of 60 feet. If someone else attacks with a pebble, that attacker adds your spellcasting ability modifier, not the attacker's, to the attack roll. On a hit, the target takes bludgeoning damage equal to [[/r 1d6]] + your spellcasting ability modifier. Whether the attack hits or misses, the spell then ends on the stone.</p><div class=\"rd__spc-inline-post\">&nbsp;</div><p>If you cast this spell again, the spell ends on any pebbles still affected by your previous casting.</p>"
    },    
    "quantity": 3,
    "weight": 0.5,        
    "rarity": "common",
    "identified": true,
    "activation": {
      "type": "action",
      "cost": 1,
      "condition": ""
    },
    "target": {
      "value": 1,
      "width": null,
      "units": "",
      "type": "creature"
    },    
    "range": {
      "value": 60,
      "long": null,
      "units": "ft"
    },
    "uses": {
      "value": 1,
      "max": "1",
      "per": "charges",
      "autoDestroy": true
    },
    "ability": "",
    "actionType": "rsak",
    "attackBonus": `${cast_mod}`,
    "damage": {
      "parts": [
        [
          `1d6 + ${cast_mod}`,
          "bludgeoning"
        ]
      ],
      "versatile": ""
    },
    "consumableType": "ammo"
  },  
  "flags": {    
    "midi-qol": {
      "onUseMacroName": "",
      "effectActivation": false
    }
  }
}];
    await tactor.createEmbeddedDocuments("Item", itemData);
}
if (args[0] === "off") {
    let itemz = tactor.data.items.find(i => i.name === itemD.name && i.type === "consumable");
    if (itemz) await tactor.deleteEmbeddedDocuments('Item', [itemz.id]);
}
